app.factory('notify', ['jquery', function($) {
    return {
        info   : function(text) { return _notify({message: text}, {type: 'info'}); },
        success: function(text) { return _notify({message: text}, {type: 'success'}); },
        danger : function(text) { return _notify({message: text}, {type: 'danger'}); },
        warning: function(text) { return _notify({message: text}, {type: 'warning'}); },
        error  : function(text) { return _notify({message: text}, {type: 'danger'}); },
        notify : _notify
    };
    
    function _notify(options, settings) {
        $.notify(options, settings);
    };
}]);